import React, { FC, useState, ChangeEvent, FormEvent } from "react";
import { Form } from "../components";
import FormCard from "../components/FormCardBody"
import FormInput from "../components/FormInput";
import FormButtom from "../components/FormButton";
import { Heading, Paragraph, FormCardBody, HorizontalLine } from "../styling/css";
import axios from "axios";
import swal from "sweetalert";
import validator from "validator"



type CreateAdminProps = {};

const CreateAdmin: FC = (props: CreateAdminProps) => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    stack: "",
    assignrole: "",
    squad: ""
  });

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleBlur = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    try {
      e.preventDefault();

      axios.post(`${process.env.REACT_APP_BaseUrl}/superadmin/create`, formData);

      swal("Success", "Admin has been successfully created", "success");
    } catch {
      swal("Error", "Something went wrong", "error");
    }
  };


  return (
    <>
      <FormCard>
        <Heading>SuperAdmin Signup</Heading>
          <div>
            <Paragraph>Fill in all required details.</Paragraph>
            <HorizontalLine />
          </div>
          <div style={{ padding: "2rem 2.5rem" }}>
            <Form onSubmit={handleSubmit}>
              <FormInput
                label="First Name"
                type="text"
                value={formData.firstName}
                name="firstName"
                onChange={(e) => handleChange(e)}
                onBlur={(e) => handleBlur(e)}
              />

              {formData.firstName.length === 0 ? (
                <span style={{ color: "red" }}></span>
              ) : formData.firstName.length > 0 &&
                formData.firstName.length < 3 ? (
                <span style={{ color: "red" }}>
                  First name must be greater than three characters
                </span>
              ) : (
                <span style={{ color: "green" }}></span>
              )}
              {/* Super Admin Sign up Validation */}

              <FormInput
                label="Last Name"
                type="text"
                value={formData.lastName}
                name="lastName"
                onChange={(e) => handleChange(e)}
                onBlur={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleBlur(e)
                }
              />
              <FormInput
                label="Email"
                type="email"
                value={formData.email}
                name="email"
                onChange={(e) => handleChange(e)}
                onBlur={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleBlur(e)
                }
              />

              {formData.email.length === 0 ? (
                <span style={{ color: "red" }}></span>
              ) : formData.email.length > 0 &&
                validator.isEmail(formData.email) ? (
                <span style={{ color: "green" }}>Valid Email</span>
              ) : (
                <span style={{ color: "red" }}>Please enter a valid email</span>
              )}

              <FormInput
                label="Stack"
                type="Stack"
                value={formData.stack}
                name="Stack"
                onChange={(e) => handleChange(e)}
                onBlur={(e: React.ChangeEvent<HTMLInputElement>) =>
                handleBlur(e)
                }
              />

              <FormInput
                label="Assign Role"
                type="password"
                value={formData.assignrole}
                name="Assign E"
                onChange={(e) => handleChange(e)}
                onBlur={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleBlur(e)
                }
              />

              <FormInput
                label="Squad"
                type="Squad"
                value={formData.squad}
                name="Squad"
                onChange={(e) => handleChange(e)}
                onBlur={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleBlur(e)
                }
              />

              <FormButtom text="Create Super Admin" />
            </Form>
          </div>
      </FormCard>
    </>
  );

};

export default CreateAdmin;
