import React from "react"

type Options = {

}

type SelectFieldProps = {
    name?: string;
    id?: number;
    text?: string;
    value?: string;
    optionData?: Record<string, string>[];
    label?: string;
    placeholder?: string;
}

const SelectField = ({
    name,
    id,
    text,
    value,
    optionData,
    label,
    placeholder,
}: SelectFieldProps) => {

    return (
        <div className="select-group">
            {label && <label className={"form--label"}>{label}</label>}
            <select 
                name={name}
                placeholder={placeholder}
                value={value}
            >

                    {
                        optionData && optionData.map
                        (
                            (item, value) => (
                                <option>
                                    {item.role}
                                </option>
                            )
                        )
                    }

            </select>
        </div>
    )
}

export default SelectField
