import React, { ChangeEvent, FormEvent, useState } from "react";
import { Form } from "../../components";
import FormCard from "../../components/FormCardBody";
import ProfileCard from "../../components/ProfileCardBody";
import FormInput from "../../components/FormInput";
import "./Profile_Content.css";
import { Heading, HorizontalLine, Paragraph } from "../../styling/css";
import CircularImage from "../../components/circular_image/CircularImage";
import CrackedImg from "../../assets/img_crack.jpg"
import FormButton from "../../components/FormButton";

const Profile_Content = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    stack: "",
    squad: "",
  });

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {};

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleBlur = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <>
      <Heading>
        Profile
      </Heading>
      <ProfileCard style={{ color: "red" }}>
        <div>
          <Paragraph>ONLY YOU CAN EDIT YOUR PROFILE.</Paragraph>
          <HorizontalLine style={{ marginBottom: "2rem" }} />
          <CircularImage
            top_circle={<img src={CrackedImg} alt='crackedimg' />}
            bottom_circle={""}
            text={"Cherechi Orika"}
          />
        </div>
        <Form onSubmit={handleSubmit}>
          <FormInput
            label="First Name"
            type="text"
            value={formData.firstName}
            name="firstName"
            onChange={(e) => handleChange(e)}
            onBlur={handleBlur}
            placeholder="First Name"
          />
          <FormInput
            label="Last Name"
            type="text"
            value={formData.lastName}
            name="lastName"
            onChange={(e) => handleChange(e)}
            onBlur={handleBlur}
            placeholder="Last Name"
          />
          <FormInput
            label="Email"
            type="email"
            value={formData.email}
            name="email"
            onChange={(e) => handleChange(e)}
            onBlur={handleBlur}
            placeholder="Email"
          />
          <FormInput
            label="Stack"
            type="text"
            value={formData.stack}
            name="stack"
            onChange={(e) => handleChange(e)}
            onBlur={handleBlur}
            placeholder="Stack"
          />
          <FormInput
            label="Squad"
            type="text"
            value={formData.squad}
            name="squad"
            onChange={(e) => handleChange(e)}
            onBlur={handleBlur}
            placeholder="Squad"
          />
        </Form>
        <FormButton text={"save"}/>
      </ProfileCard>
    </>
  );
};

export default Profile_Content;
