import React, { FC, useState, ChangeEvent, FormEvent } from "react";
import { Form } from "../components";
import FormCard from "../components/FormCardBody"
import FormInput from "../components/FormInput";
import FormButtom from "../components/FormButton";
import { Heading, Paragraph, FormCardBody, HorizontalLine } from "../styling/css";
import axios from "axios";
import swal from "sweetalert";
import validator from "validator"



type CreateAdminProps = {};

const CreateAdmin: FC = (props: CreateAdminProps) => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    stack: "",
    assignrole: "",
    squad: ""
  });

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleBlur = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    try {
      e.preventDefault();

      axios.post(`${process.env.REACT_APP_BaseUrl}/superadmin/create`, formData);

      swal("Success", "Admin has been successfully created", "success");
    } catch {
      swal("Error", "Something went wrong", "error");
    }
  };



};

export default CreateAdmin;
