import React, { FC, useState, ChangeEvent, FormEvent } from "react";
import { FormInput, FormCard, FormButton, Form } from "../components";
import { Heading, Paragraph, HorizontalLine } from "../styling/css";
import axios from "axios";
import swal from "sweetalert";
type SuperAdminSignUpProps = {};
const SuperAdminSignUp: FC = (props: SuperAdminSignUpProps) => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });
  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };
  const handleBlur = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    try {
      e.preventDefault();
      axios.post(`${process.env.REACT_APP_BaseUrl}/api/super-admin-signup`, formData);
      swal("Success", "You have successfully signed in", "success");
    } catch {
      swal("Error", "Something went wrong", "error");
    }
  };
  return (
    <>
      <Heading>SuperAdmin Signup</Heading>
      <FormCard>
        <div>
          <Paragraph>Fill in all required details.</Paragraph>
          <HorizontalLine />
        </div>
        <div style={{ padding: "2rem 2.5rem" }}>
          <Form onSubmit={handleSubmit}>
            <FormInput
              label="First Name"
              type="text"
              value={formData.firstName}
              name="firstName"
              onChange={(e) => handleChange(e)}
              onBlur={(e) => handleBlur(e)}
            />
            <FormInput
              label="Last Name"
              type="text"
              value={formData.lastName}
              name="lastName"
              onChange={(e) => handleChange(e)}
              onBlur={(e) => handleBlur(e)}
            />
            <FormInput
              label="Email"
              type="email"
              value={formData.email}
              name="email"
              onChange={(e) => handleChange(e)}
              onBlur={(e) => handleBlur(e)}
            />
            <FormInput
              label="Password"
              type="password"
              value={formData.password}
              name="password"
              onChange={(e) => handleChange(e)}
              onBlur={(e) => handleBlur(e)}
            />
            <FormButtons text="Create Super Admin" />
          </Form>
        </div>
      </FormCard>
    </>
  );
};
export default SuperAdminSignUp;
