  pic?: any;
  text?: string;
  stackRole?: string;
  imageHeight?: string;
  imageWidth?: string;
  textFontSize?: string;
  textColor?: string;
  textFontFamily?: string;
  textFontWeight?: string;
  textBackgroundColor?: string;
  alignText?: string;
};

const Profile = ({
  pic,
  text,
  stackRole,
  imageHeight,
  imageWidth,
  textFontSize,
  textColor,
  textFontFamily,
  textFontWeight,
  textBackgroundColor,
  alignText,
}: ProfileProps) => {
  const [isOpen, setIsOpen] = useState(false);
  const [formData, setFormData] = useState<FormData>(new FormData());

  const toggleClick = () => {
    setIsOpen(!isOpen);
    // console.log(isOpen);
  };

  const handleSubmit = (e: any) => {
